import React from "react";
import { WidthType, VerticalAlign } from "docx";
import { cmToTwip } from "./metrics";
import { useResume } from "./hooks";
import { useRef } from "react";
import { useLayoutEffect } from "react";
const { STYLES } = require("./styles");

const hasAvatar = require("./utils/has-avatar");
const { columnWidths } = require("./widths");

export const Header = () => {
  const { resume } = useResume();
  //if (!hasAvatar(resume)) return <Title />;

  // we can use layoutEffect and refs to access docx elements instances
  // for various hacks
  const avatarRef = useRef(null);
  const titleRef = useRef(null);
  useLayoutEffect(() => {
    avatarRef.current.properties.setWidth(columnWidths.avatar, WidthType.DXA);
    titleRef.current.properties.setWidth(columnWidths.title, WidthType.DXA);
  });
  return (
    <table
      width={{
        size: columnWidths.table,
        type: WidthType.DXA,
      }}
      columnWidths={[columnWidths.avatar, columnWidths.title]}
      rows={[]}
    >
      <tablerow>
        <tablecell
          ref={avatarRef}
          width={{
            size: columnWidths.avatar,
            type: WidthType.DXA,
          }}
          margins={{
            right: cmToTwip(0.5),
          }}
        >
          <Avatar />
        </tablecell>
        <tablecell
          ref={titleRef}
          width={{
            size: columnWidths.title,
            type: WidthType.DXA,
          }}
          verticalAlign={VerticalAlign.CENTER}
        >
          <Title />
        </tablecell>
      </tablerow>
    </table>
  );
};

export const Title = () => {
  return <p style={STYLES.name}>ФИО</p>;
};

const imgData =
  "data:image/gif;base64,";

export const Avatar = ({ size = 65 }) => {
  return (
    <p>
      <img src={imgData} width={size} height={size} />
    </p>
  );
};
